post_headers = \
    "POST /Admission_List.aspx HTTP/1.1\r\n" + \
    "Host: www.nhyz.org\r\n" + \
    "Connection: close\r\n" + \
    "Accept: text/html,application/xhtml+xml,application/xml;q=0.9,*/*;q=0.8\r\n" + \
    "User-Agent: Mozilla/5.0 (Windows NT 6.1; WOW64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/39.0.2171.95 Safari/537.36\r\n" + \
    "Accept-Language: en-US,en;q=0.8,ja;q=0.6,th;q=0.4,zh-CN;q=0.2,zh;q=0.2,zh-TW;q=0.2,es;q=0.2\r\n" + \
    "Content-Length: %s\r\n" % len(post_datas) + \
    "\r\n" + \
    post_datas



if __name__ == "__main__":
    s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)
    s.connect(("www.nhyz.org", 80))

    s.send(post_headers)

    buffer = []
    while True:
        d = s.recv(1024)
        if d:
            buffer.append(d)
        else:
            break
    s.close()

    data = ''.join(buffer)
    data = data.decode("gbk").encode("utf-8")

    with open("2013_list.html", "w") as f:
        f.write(data)



